import { useRef, useState } from "react";

const Test = () =>{
    // 더미데이터
   
    const [item,setBannerStyle] = useState([
        {
            url: "https://www.dunkindonuts.co.kr/menu/view?cat=1&sub=1&id=5605",
            view: "/resources/img/TRASnuAzDDUWylEDcKeE8jrIc1f5sTUpBdIeECsH.png",
    
        },
        { 
            url: "https://www.dunkindonuts.co.kr/menu/viewProductCat?cat=3&sub=28&id=70",
            view: "/resources/img/ouqUlooZk8fmPnZIMxreXmPG6LAZ5hE6ANVJZ84E.png",
    
        },
        {
            url: "https://blog.naver.com/dunkin_kr/223580576950",
            view: "/resources/img/NhKu3wzNU0AOc48qysjJtksUVlbJS6bDLBjj7EEW.png",
    
        }
    ]);
    
    const length = (item) =>{
        console.log(item.length)
        console.log(item[0])
    } 
    const select = (item)=>{
        for( let i=0; item.length>i;i++){
            console.log(item[i]["view"])
            console.log(item[i]["url"])
        }
    }
    const indexRef= useRef(0)

    const slide = ()=>{
        indexRef.current=0
        let num = indexRef.current
            console.log(item[num].url)
        return(
            "div",{
                className :"w-full h-full rounded-[clamp(20px,6.48vw,95px)] shadow-[2px_2px_18px_#A88D76] overflow-hidden",
                children:("div",{
                        className : "flex w-full ",
                        children:("a",{
                                className:"block flex-none w-full h-full aspect-[1566/749] relative bannerInner1",
                                href:item[num].url,
                                style:{
                                    transform: "translate3d(4377px, 0px, 0px)"
                                },
                                children:("img",{
                                        src:item[num].view,
                                        alt:"banner",
                                        className:"w-full h-full object-cover aspect-[1566/749]"
                                            }
                                        )
                                    }
                                )
                            }
                        )
                    },
                    "div",{
                        className:"absolute bottom-[42px] left-1/2 -translate-x-1/2",
                        children:(
                            "div",{
                                className:"flex justify-center",
                                children:(
                                    item.map((index) =>(
                                        "button",{
                                            type:"button",
                                            className:"px-[4px] py-[7px]" ,
                                            onclick:indexRef.current=index,
                                            children:(
                                                "div",{
                                                    className:"bg-white rounded-full h-[7px] transition-all w-[7px] opacity-40 "+num==index?'!opacity-100 !w-[20px]':""
                                                }
                                            )
                                        }
                                    ))
                                ),
                            } 
                        )
                    }
            
        )

    }

    return {length,select,slide}

}
export default Test;
