import { useRef, useState } from "react";

const Test = () =>{
    // 더미데이터
   

    const length = (item) =>{
        console.log(item.length)
        console.log(item[0])
    } 
    const select = (item)=>{
        for( let i=0; item.length>i;i++){
            console.log(item[i]["img"])
            console.log(item[i]["url"])
        }
    }
    const indexRef= useRef(0)

    const slide = (item)=>{
        indexRef.current=0
        return(
            "div",{
                className :"w-full h-full rounded-[clamp(20px,6.48vw,95px)] shadow-[2px_2px_18px_#A88D76] overflow-hidden",
                children:(
                    "div",{
                        className : "flex w-full ",
                        children:(
                            "a",{
                                className:"block flex-none w-full h-full aspect-[1566/749] relative bannerInner1",
                                href:item[indexRef.current]["url"],
                                style:{
                                    transform: "translate3d(4377px, 0px, 0px)"
                                },
                                children:(
                                    "img",{
                                        src:item[indexRef.current]["img"],
                                        alt:"banner",
                                        className:"w-full h-full object-cover aspect-[1566/749]"
                                            }
                                        )
                                    }
                                )
                            }
                        )
                    },
                    "div",{
                        className:"absolute bottom-[42px] left-1/2 -translate-x-1/2",
                        children:(
                            "div",{
                                className:"flex justify-center",
                                children:(
                                    item.map((index) =>(
                                        "button",{
                                            type:"button",
                                            className:"px-[4px] py-[7px]" ,
                                            onclick:indexRef.current=index,
                                            children:(
                                                "div",{
                                                    className:"bg-white rounded-full h-[7px] transition-all w-[7px] opacity-40 "+indexRef.current==index?'!opacity-100 !w-[20px]':""
                                                }
                                            )
                                        }
                                    ))
                                ),
                            } 
                        )
                    }
            
        )

    }

    return {length,select,slide}

}
export default Test;
