const mainSlideCall = () =>{

    const [bannerOrder,setBanner] = useState(1);
    const bannerRef = useRef(1)
  
    const [mainorder,setMaim] = useState(1);
    const MainRef = useRef(1)

    const [topBannerSet, setTopBannerSet] = useState(false);
    const [mainBannerSet, setMainBannerSet] = useState(false);

    const setOrder = useCallback((num,item) => {
        if(item=="top"){
            bannerRef.current = num;
            setBanner(num)
        }else{item=="main"}{
            bannerRef.current = num;
            setBanner(num)
        }
  }, []);

  const bannerPlay = ()=>{
    return setInterval(() => {     
        slideCall(bannerRef.current==3?1:bannerRef.current+1,"top",1500);
    }, 7500); 
 }
 const mainPlay = ()=>{
    return setInterval(() => {     
        slideCall(MainRef.current==6?1:MainRef.current+1,"main",1500);
    }, 7500); 
 }
 const onClickButton = (num,type)=>{
    setOrder(num,type);
    clearInterval((type=="top"?bannerPlay():mainPlay()));
}
    const slideCall = (num,item,time) =>{
        (item=="top"?setTopBannerSet(true):setMainBannerSet(true))
        setTimeout(()=>{
        (item=="top"?setTopBannerSet(false):setMainBannerSet(false))
            setOrder(num,item);
        },time)
            
        };
    return [bannerOrder,mainorder,topBannerSet,mainBannerSet,bannerPlay,mainPlay,onClickButton];    


}
export default mainSlideCall;
