const mainSlideCall = () =>{

    const slideCall = (set,update,time) =>{

        if(typeof set=="function"&&typeof update=="function"){

            set(true)
            setTimeout(()=>{
                set(false)
                update();
            },time)
        }
    };
    
    return [slideCall];    
}

export default mainSlideCall;
