        type==="top"?bannerRef.current =num: MainRef.current = num
        type==="top"?setBanner(num): setMaim(num)
  }, []);



 const onPlay = ()=>{
    //console.log(type)
    bannerPlayRef.current = setInterval(() => {  
        console.log("Interval")   
      slideCall(bannerRef.current===3?1:bannerRef.current+1,"top",500);
  },6500)
    
};
 const onClickButton = (num,type)=>{
    //console.log(type)
    
    if(type==="top"){
        clearInterval(bannerPlayRef.current)
        slideCall(num,type,500)
        onPlay()
    }else if(type==="main"){
        clearInterval(mainPlay())
        slideCall(num,type,500)
        mainPlay()
    }
    setTimeout(()=>{
        slideCall(num,type,500)
    },300)
    
};
 const mainPlay = useCallback(()=>{
    //console.log("mainPlay")   
    return setInterval(() => {     
        slideCall(MainRef.current===6?1:MainRef.current+1,"main",500);
    },6500); 
 },[])
 const slidePlay=()=>{
