            type==="top"?setTopBannerSet(true):setMainBannerSet(true)
            setTimeout(()=>{
                type==="top"?setTopBannerSet(false):setMainBannerSet(false)
                setOrder(num,type);
            },time)     
        };
        const slideReCall = (num,type,time) =>{
            type==="top"?setTopBannerSet(true):setMainBannerSet(true)
            setTimeout(()=>{
                type==="top"?setTopBannerSet(false):setMainBannerSet(false)
                setOrder(num-1,type);
            },time)     
        };
    return [bannerOrder,mainorder,topBannerSet,mainBannerSet,slideData,state,{addSideData,slidePlay,onClickButton,select,changre,bannerPlay,changeBanner},];    



}
